mapper.select:
select count(*)
from stu_crse_list scl left join question qstn on scl.QSTN_ID = qstn.QSTN_ID left join subj_section sctn on sctn.SUBJ_SCTN_ID = qstn.SUBJ_SCTN_ID left join stu_crse_test sct on sct.sct_id = scl.sct_id
where sct.crse_id = #{crseId,jdbcType=VARCHAR} and sct.user_id = #{userId,jdbcType=VARCHAR} and scl_result != '0' and qstn.SUBJ_SCTN_ID = #{sctnId,jdbcType=VARCHAR}

mapper.update:
update stu_crse_list
set sct_id = #{sctId,jdbcType=VARCHAR},
  QSTN_ID = #{qstnId,jdbcType=VARCHAR},
  scl_content = #{sclContent,jdbcType=VARCHAR},
  scl_result = #{sclResult,jdbcType=VARCHAR}
where scl_id = #{sclId,jdbcType=VARCHAR}
