mapper.select:
select SUBJ_UNIT_ID, su.SUBJ_ID, su.TCH_ID, SUBJ_UNIT_TITLE, SUBJ_UNIT_NUM, SUBJ_UNIT_CODE, SUBJ_UNIT_INFO, SUBJ_UNIT_LOGO_URL , UNIT_COUNT , tch.TCH_NAME , sc.SUBJ_TITLE from subj_unit su left join teacher tch on su.TCH_ID = tch.TCH_ID left join subject_course sc on su.SUBJ_ID = sc.SUBJ_ID
and su.SUBJ_ID = #{subjId}
order by sc.SUBJ_NUM, SUBJ_UNIT_NUM

select SUBJ_UNIT_ID, su.SUBJ_ID, su.TCH_ID, SUBJ_UNIT_TITLE, SUBJ_UNIT_NUM, SUBJ_UNIT_CODE, SUBJ_UNIT_INFO, SUBJ_UNIT_LOGO_URL , UNIT_COUNT , tch.TCH_NAME , sc.SUBJ_TITLE from subj_unit su left join teacher tch on su.TCH_ID = tch.TCH_ID left join subject_course sc on su.SUBJ_ID = sc.SUBJ_ID
#{subjId}
order by sc.SUBJ_NUM, SUBJ_UNIT_NUM

